import React, {useState, useEffect} from 'react';
import {createStudent, updateStudent, uploadPhoto, createEvaluation, last5} from '../services/api';

export default function StudentModal({student, token, username, onClose}){
  const isNew = !student.id;
  const [form, setForm] = useState({name:'', series:'', formation:'FUND1'});
  const [file, setFile] = useState(null);
  const [report, setReport] = useState([]);
  useEffect(()=>{ if(!isNew){ setForm({name:student.name, series:student.series, formation:student.formation}); loadReport(); } },[]);
  async function save(){
    if(isNew){
      await createStudent(form, token);
      onClose();
    } else {
      await updateStudent(student.id, form, token);
      if(file){ await uploadPhoto(student.id, file, token); }
      onClose();
    }
  }
  async function loadReport(){
    try{
      const r = await last5(student.id, token);
      setReport(r);
    }catch(e){}
  }
  // evaluation emoji-based
  const [evalVals, setEvalVals] = useState({ass:3, part:3, resp:3, soc:3, obs:''});
  async function submitEval(){
    const payload = {
      student: { id: student.id },
      assiduidade: evalVals.ass,
      participacao: evalVals.part,
      responsabilidade: evalVals.resp,
      sociabilidade: evalVals.soc,
      observation: evalVals.obs
    };
    await createEvaluation(payload, token);
    setEvalVals({...evalVals, obs:''});
    loadReport();
  }
  return (
    <div style={{position:'fixed',left:20,top:20,right:20,bottom:20,background:'#fff',padding:20,overflow:'auto',boxShadow:'0 0 10px rgba(0,0,0,0.3)'}}>
      <button onClick={onClose}>Fechar</button>
      <h3>{isNew? 'Novo aluno' : 'Editar aluno'}</h3>
      <div>
        <div><input placeholder='Nome' value={form.name} onChange={e=>setForm({...form, name:e.target.value})}/></div>
        <div><input placeholder='SÃ©rie' value={form.series} onChange={e=>setForm({...form, series:e.target.value})}/></div>
        <div>
          <select value={form.formation} onChange={e=>setForm({...form, formation:e.target.value})}>
            <option value='FUND1'>Fundamental 1</option>
            <option value='FUND2'>Fundamental 2</option>
            <option value='MEDIO'>Ensino MÃ©dio</option>
          </select>
        </div>
        <div>
          <input type='file' onChange={e=>setFile(e.target.files[0])}/>
        </div>
        <div><button onClick={save}>Salvar</button></div>
      </div>

      {!isNew && <div style={{marginTop:20}}>
        <h4>AvaliaÃ§Ã£o lÃºdica</h4>
        <div>
          <label>Assiduidade: </label>
          {[1,2,3,4,5].map(n=> <button key={n} onClick={()=>setEvalVals({...evalVals, ass:n})}>{'ðŸ˜€'.repeat(n<=3?1:1)}</button>)}
          <span>{evalVals.ass}</span>
        </div>
        <div>
          <label>ParticipaÃ§Ã£o: </label>
          {[1,2,3,4,5].map(n=> <button key={n} onClick={()=>setEvalVals({...evalVals, part:n})}>{'ðŸ˜€'.repeat(n<=3?1:1)}</button>)}
          <span>{evalVals.part}</span>
        </div>
        <div>
          <label>Responsabilidade: </label>
          {[1,2,3,4,5].map(n=> <button key={n} onClick={()=>setEvalVals({...evalVals, resp:n})}>{'ðŸ˜€'.repeat(n<=3?1:1)}</button>)}
          <span>{evalVals.resp}</span>
        </div>
        <div>
          <label>Sociabilidade: </label>
          {[1,2,3,4,5].map(n=> <button key={n} onClick={()=>setEvalVals({...evalVals, soc:n})}>{'ðŸ˜€'.repeat(n<=3?1:1)}</button>)}
          <span>{evalVals.soc}</span>
        </div>
        <div>
          <textarea placeholder='ObservaÃ§Ã£o' value={evalVals.obs} onChange={e=>setEvalVals({...evalVals, obs:e.target.value})}></textarea>
        </div>
        <div><button onClick={submitEval}>Enviar avaliaÃ§Ã£o ({username})</button></div>

        <h4>Ãšltimas 5 avaliaÃ§Ãµes</h4>
        <div>
          {report.map(r=> <div key={r.id} style={{borderTop:'1px solid #ddd',paddingTop:8}}>
            <div><strong>{r.evaluator}</strong> â€” {new Date(r.dateTime).toLocaleString()}</div>
            <div>MÃ©dia: {((r.assiduidade + r.participacao + r.responsabilidade + r.sociabilidade)/4).toFixed(2)}</div>
            <div>{r.observation}</div>
          </div>)}
        </div>
      </div>}
    </div>
  );
}
