import React, {useState} from 'react';
import {login} from '../services/api';

export default function Login({onLogin}){
  const [username,setUsername]=useState('');
  const [password,setPassword]=useState('');
  const [err,setErr]=useState('');
  async function submit(e){
    e.preventDefault();
    try{
      const r = await login(username,password);
      onLogin(r.token, r.username);
    }catch(err){
      setErr('Login falhou');
    }
  }
  return (
    <form onSubmit={submit} style={{maxWidth:360}}>
      <div><input placeholder="UsuÃ¡rio" value={username} onChange={e=>setUsername(e.target.value)} required/></div>
      <div><input placeholder="Senha" type="password" value={password} onChange={e=>setPassword(e.target.value)} required/></div>
      <div><button type="submit">Entrar</button></div>
      {err && <div style={{color:'red'}}>{err}</div>}
    </form>
  );
}
