import React, {useState} from 'react';
import Login from './components/Login';
import Carometro from './components/Carometro';

export default function App(){
  const [token, setToken] = useState(localStorage.getItem('token')||'');
  const [username, setUsername] = useState(localStorage.getItem('username')||'');
  function onLogin(tok, user){
    setToken(tok); setUsername(user);
    localStorage.setItem('token', tok); localStorage.setItem('username', user);
  }
  function onLogout(){
    setToken(''); setUsername('');
    localStorage.removeItem('token'); localStorage.removeItem('username');
  }
  return (
    <div style={{padding:20,fontFamily:'Arial'}}>
      <h1>Carômetro — Avaliação Lúdica</h1>
      {!token ? <Login onLogin={onLogin}/> : <div>
        <div style={{display:'flex', justifyContent:'space-between', alignItems:'center'}}>
          <div>Logado como <strong>{username}</strong></div>
          <button onClick={onLogout}>Sair</button>
        </div>
        <Carometro token={token} username={username}/>
      </div>}
    </div>
  );
}
