import React, { useState } from "react";
import { login } from "../services/api";

export default function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [err, setErr] = useState("");

  async function submit(e) {
    e.preventDefault();
    try {
      const r = await login(username, password);
      onLogin(r.token, r.username);
    } catch (err) {
      setErr("Usuário ou senha incorretos");
    }
  }

  const styles = {
    container: {
      width: "100%",
      minHeight: "100vh",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      background: "#f3f4f6",
      padding: 20,
    },
    card: {
      background: "white",
      width: "100%",
      maxWidth: 380,
      padding: "32px 28px",
      borderRadius: 14,
      boxShadow: "0 6px 18px rgba(0,0,0,0.12)",
      display: "flex",
      flexDirection: "column",
      gap: 18,
      transition: "0.3s",
    },
    title: {
      fontSize: 26,
      fontWeight: 700,
      textAlign: "center",
      marginBottom: 10,
      color: "#1f2937",
    },
    input: {
      width: "100%",
      padding: "12px 14px",
      fontSize: 15,
      border: "1px solid #d1d5db",
      borderRadius: 8,
      outline: "none",
      transition: "0.2s border-color, 0.2s box-shadow",
    },
    button: {
      width: "100%",
      padding: "12px 14px",
      background: "#2563eb",
      color: "white",
      fontSize: 16,
      fontWeight: 600,
      border: "none",
      borderRadius: 8,
      cursor: "pointer",
      transition: "0.2s background",
    },
    error: {
      color: "red",
      fontSize: 14,
      textAlign: "center",
      marginTop: -5,
    },
  };

  return (
    <div style={styles.container}>
      <form style={styles.card} onSubmit={submit}>
        <div style={styles.title}>Área Administrativa</div>

        <input
          style={{
            ...styles.input,
            borderColor: "#d1d5db",
          }}
          placeholder="Usuário"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          onFocus={(e) => {
            e.target.style.borderColor = "#2563eb";
            e.target.style.boxShadow = "0 0 4px rgba(37, 99, 235, 0.4)";
          }}
          onBlur={(e) => {
            e.target.style.borderColor = "#d1d5db";
            e.target.style.boxShadow = "none";
          }}
          required
        />

        <input
          style={{
            ...styles.input,
            borderColor: "#d1d5db",
          }}
          placeholder="Senha"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          onFocus={(e) => {
            e.target.style.borderColor = "#2563eb";
            e.target.style.boxShadow = "0 0 4px rgba(37, 99, 235, 0.4)";
          }}
          onBlur={(e) => {
            e.target.style.borderColor = "#d1d5db";
            e.target.style.boxShadow = "none";
          }}
          required
        />

        <button
          type="submit"
          style={styles.button}
          onMouseOver={(e) => (e.target.style.background = "#1d4ed8")}
          onMouseOut={(e) => (e.target.style.background = "#2563eb")}
        >
          Entrar
        </button>

        {err && <div style={styles.error}>{err}</div>}
      </form>
    </div>
  );
}
