import React, { useState, useEffect } from 'react';
import StudentModal from './StudentModal';
//import { getStudents } from '../services/api';
import { fetchStudents } from '../services/api';


export default function Carometro({ token, username }) {
  const [students, setStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Carrega alunos da API
  useEffect(() => {
    async function loadStudents() {
      try {
        const data = await getStudents(token);
        setStudents(Array.isArray(data) ? data : []); // garante array
      } catch (e) {
        console.error('Erro ao carregar alunos:', e);
        setStudents([]);
      }
    }
    loadStudents();
  }, [token]);

  function openModal(student = null) {
    setSelectedStudent(student);
    setShowModal(true);
  }

  function closeModal() {
    setSelectedStudent(null);
    setShowModal(false);
    // recarrega alunos após criar/editar
    async function refresh() {
      try {
        const data = await getStudents(token);
        setStudents(Array.isArray(data) ? data : []);
      } catch (e) {
        setStudents([]);
      }
    }
    refresh();
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>Área Administrativa - Carômetro</h2>
      <button onClick={() => openModal()}>Novo Aluno</button>

      <div style={{ marginTop: 20 }}>
        {students.length > 0 ? (
          students.map((s) => (
            <div
              key={s.id}
              style={{
                display: 'flex',
                alignItems: 'center',
                borderBottom: '1px solid #ddd',
                padding: 10
              }}
            >
              <img
                src={s.photoPath || '/uploads/default.png'}
                alt={s.name}
                style={{ width: 50, height: 50, borderRadius: '50%', marginRight: 10 }}
              />
              <div style={{ flex: 1 }}>
                <div>
                  <strong>{s.name}</strong> - {s.series} ({s.formation})
                </div>
              </div>
              <button onClick={() => openModal(s)}>Editar</button>
            </div>
          ))
        ) : (
          <div>Nenhum aluno encontrado.</div>
        )}
      </div>

      {showModal && (
        <StudentModal
          student={selectedStudent}
          token={token}
          username={username}
          onClose={closeModal}
        />
      )}
    </div>
  );
}
